```cds
using { managed } from '@sap/cds/common';
namespace sflight;

aspect custom.managed {
  createdAt : managed:createdAt;
  createdBy : managed:createdBy;
  LastChangedAt : managed:modifiedAt;
  LastChangedBy : managed:modifiedBy;
}

entity BookingSupplement  : custom.managed {
  key BookSupplUUID : UUID;
  BookingSupplementID : Integer ;
  Price : Decimal(16, 3);
}
```

